mapper.select:
SELECT c.id, sid, c.name, createtime, updatetime, specification, val, img, introduce, price, malllink, store, isstore, place, LEVEL, sealing, updownlink, brand, VERSION, remark1, remark2, remark3, s.name sname,s.lid,l.name lname FROM t_component c,t_subclass s ,t_largeclass l
c.sid=s.id AND s.lid=l.id
and id=#{id}
and sid=#{sid}
and c.name like '%${name}%'

SELECT c.id cid, sid, c.name cname, createtime, updatetime, specification, val, img, introduce, price, malllink, store, isstore, place, LEVEL, sealing, updownlink, brand, VERSION, remark1, remark2, remark3,
s.`lid` slid,s.`name` sname, l.`name` lname FROM t_component c,t_subclass s,t_largeclass l
WHERE c.`sid`=s.`id` AND s.`lid`=l.`id` AND c.id=#{value}
